mapper.select:
SELECT user_id, user_email, user_pw, user_name, profile_img
  FROM tbl_user

  user_email = #{userEmail}
  AND user_pw = #{userPw}
  AND user_id = #{userId}

mapper.update:
UPDATE tbl_user

    user_email = #{userEmail}
    , user_name = #{userName}

 WHERE user_id = #{userId}

UPDATE tbl_user
   SET profile_img = #{profileImg}
 WHERE user_id = #{userId}

UPDATE tbl_user
   SET user_pw = #{userPw}
 WHERE user_id = #{userId}
